parameters.jsCode:
// This object will temporarily store our merged records, using the run_id as the key.
const groupedRuns = {};

// The previous node provides a flat list of all feedback items.
// We can loop through them directly.
for (const item of $input.all()) {
  // Ensure the item has a json property before proceeding.
  if (!item.json) {
    continue;
  }
  const runId = item.json.run_id;

  // Skip any items that might not have a run_id.
  if (!runId) {
    continue;
  }

  // If we haven't seen this run_id before, create the base record for it.
  if (!groupedRuns[runId]) {
    groupedRuns[runId] = {
      run_id: item.json.run_id,
      session_id: item.json.session_id,
      trace_id: item.json.trace_id,
      created_at: item.json.created_at, // Note: This will be the timestamp of the first feedback item encountered for this run.
      feedback_source: item.json.feedback_source,
    };
  }

  // Get the specific feedback key (e.g., "note", "trip_validity").
  const key = item.json.key;

  // Add the feedback as a new property on our grouped record.
  // We handle the "note" key differently, as its main content is in the "comment" field.
  if (key === 'note') {
    groupedRuns[runId][key] = item.json.comment;
  } else {
    // For all other keys, we create an object containing both the value and the score.
    groupedRuns[runId][key] = {
      value: item.json.value,
      score: item.json.score
    };
  }
}

// Convert the temporary object into an array of all merged records.
const allMergedRecords = Object.values(groupedRuns);

// --- CORRECTED FILTER ---
// Keep records that meet EITHER of the following criteria:
// 1. The 'verdict' is 'False Positive'.
// OR
// 2. The 'appropriateness' is 'UNNECESSARY'.
const filteredRecords = allMergedRecords.filter(record => {
  // Check for the first condition (False Positive), ignoring 'agreed_with_judge'.
  const isFalsePositive = 
    record.hasOwnProperty('verdict') && record.verdict.value === 'False Positive';

  // Check for the second condition (Unnecessary).
  const isUnnecessary = 
    record.hasOwnProperty('appropriateness') && record.appropriateness.value === 'UNNECESSARY';

  // Keep the record if EITHER condition is true.
  return isFalsePositive || isUnnecessary;
});

// Return the final, filtered list.
return filteredRecords;

