const allFeedback = $input.all();
const runIds = allFeedback.map(item => item.json.run_id);
const requestBody = { id: runIds };
return {
  json: {
    bodyForRequest: requestBody,
    originalFeedback: allFeedback.map(item => item.json) // We'll need this later
  }
};

// This node decodes the Base64 encoded 'original_guard_prompt'
// received from the 'Assemble Case File' node into a readable string.

// It's crucial for the LLM to receive plain text for proper processing.

// Iterate over each item received by this node.
for (const item of items) {
  try {
    // Get the stringified JSON from the 'original_guard_prompt' field.
    // Use optional chaining (?.) to prevent errors if the field is missing.
    const stringifiedJson = item.json.original_guard_prompt;

    // Check if the stringified JSON exists and is a string.
    if (typeof stringifiedJson === 'string' && stringifiedJson) {
      // Parse the stringified JSON to get the actual object.
      const parsedData = JSON.parse(stringifiedJson);

      // Now, get the Base64 encoded string from the 'data' property within the parsed object.
      const encodedPrompt = parsedData.data;

      // Check if the encoded prompt exists and is a string.
      if (typeof encodedPrompt === 'string' && encodedPrompt) {
        // Create a Buffer from the Base64 string and then convert it to a UTF-8 string.
        // The '77u/' prefix is a Byte Order Mark (BOM) for UTF-8.
        // We'll remove it if present, as it can sometimes cause display issues.
        let decodedPrompt = Buffer.from(encodedPrompt, 'base64').toString('utf8');

        // Remove the Byte Order Mark (BOM) if it exists at the beginning of the string.
        // The BOM for UTF-8 is often represented as \uFEFF.
        if (decodedPrompt.charCodeAt(0) === 0xFEFF) {
          decodedPrompt = decodedPrompt.substring(1);
        }

        // Add the decoded prompt to the current item's JSON data.
        // We'll name this new field 'decoded_original_guard_prompt'.
        item.json.decoded_original_guard_prompt = decodedPrompt;
      } else {
        // If the encoded prompt (data property) is missing or not a string, set the decoded field to null
        // and log a warning.
        item.json.decoded_original_guard_prompt = null;
        console.warn("Warning: 'data' property within original_guard_prompt was missing or not a string. Setting decoded_original_guard_prompt to null.");
      }
    } else {
      // If the stringified JSON (original_guard_prompt) is missing or not a string, set the decoded field to null
      // and log a warning.
      item.json.decoded_original_guard_prompt = null;
      console.warn("Warning: original_guard_prompt was missing or not a string. Setting decoded_original_guard_prompt to null.");
    }
  } catch (error) {
    // Catch any errors during parsing or decoding and set the field to null,
    // also log the error for debugging.
    item.json.decoded_original_guard_prompt = null;
    console.error(`Error processing original_guard_prompt: ${error.message}`);
  }
}

// Return the modified items.
return items;

// Get the guard configurations from the 'Config' node.
const config = $('Config').first().json;
const allGuardsConfig = config.guards;

// Get the full, detailed run information from the previous node's API call.
const allRunDetails = $input.item.json.runs;

// --- CRITICAL CHANGE ---
// Instead of looking at the raw feedback, we will look at the already-merged-and-filtered
// feedback that comes out of your 'Group by Run ID' node.
const filteredMergedFeedback = $('Group by Run ID').all().map(item => item.json);

// Create a map of this filtered feedback for easy lookup using the run_id.
const feedbackMap = new Map(filteredMergedFeedback.map(fb => [fb.run_id, fb]));

// This object will hold our final, perfectly grouped data.
const groupedByGuard = {};

// Loop through each of the detailed runs we received.
for (const run of allRunDetails) {
  // Find the corresponding guard configuration for this run.
  const guardConfig = allGuardsConfig.find(g => run.name.includes(g.guardApiName));

  if (guardConfig) {
    const guardApiName = guardConfig.guardApiName;

    // If this is the first time we've seen this guard, create a new group for it.
    if (!groupedByGuard[guardApiName]) {
      groupedByGuard[guardApiName] = {
        guardApiName: guardApiName,
        guardFileName: guardConfig.guardFileName,
        // This will hold all the feedback items for this guard.
        feedbackItems: [] 
      };
    }

    // Find the full, merged feedback object that corresponds to this run.
    const feedback = feedbackMap.get(run.id);

    // If we found a matching feedback record (which we always should at this point),
    // add the ENTIRE merged feedback object to this guard's group.
    if (feedback) {
      groupedByGuard[guardApiName].feedbackItems.push({
        runDetails: run,
        feedbackDetails: feedback // This now contains ALL the feedback keys for that run
      });
    }
  }
}

// Convert our grouped object into a simple array for the next nodes to process.
const result = Object.values(groupedByGuard);

// Return the final array in the format n8n expects.
return result.map(item => ({ json: item }));

// This function subtracts business days from a given date, operating entirely in UTC.
function subtractBusinessDaysUTC(date, days) {
  let count = 0;
  while (count < days) {
    // Move the date back by one day in UTC.
    date.setUTCDate(date.getUTCDate() - 1);
    // getUTCDay() returns 0 for Sunday, 6 for Saturday.
    if (date.getUTCDay() !== 0 && date.getUTCDay() !== 6) {
      count++;
    }
  }
  return date;
}

// Get the current date object.
const now = new Date();

// Calculate the target date by subtracting two business days in UTC.
const targetDate = subtractBusinessDaysUTC(now, 2);

// --- Create the min_created_at timestamp ---
// Set the time to the beginning of the target day in UTC.
targetDate.setUTCHours(0, 0, 0, 0);
// Get the final ISO string, which will now have T00:00:00.000Z
const min_created_at = targetDate.toISOString();

// --- Create the max_created_at timestamp ---
// Set the time to the end of the target day in UTC.
targetDate.setUTCHours(23, 59, 59, 999);
// Get the final ISO string, which will now have T23:59:59.999Z
const max_created_at = targetDate.toISOString();

// Return the calculated dates for the next node.
return {
  json: {
    min_created_at: min_created_at,
    max_created_at: max_created_at
  }
};
